import binascii

def str_to_bin(string):
	return bin(int(binascii.hexlify(string), 16));

def bin_to_str(binary):
	if binary[0:2] == '0b':
		n = int(binary, 2);
		return binascii.unhexlify('%x' % n);
	else:
		n = int('0b'+binary, 2);
		return binascii.unhexlify('%x' % n);

